from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
wheels_l = Motor(Port.E, Direction.COUNTERCLOCKWISE)
wheels_r = Motor(Port.F, Direction.CLOCKWISE)
motor_ = Motor(Port.D, Direction.CLOCKWISE)
drive_base = DriveBase(wheels_l, wheels_r, 56, 114)


# The main program starts here.
# Gideon and Atticus made a journey to pick up M12 and go to the other side.
# backed up at 1/17/2026 at 11:06am
motor_.run_until_stalled(500, Stop.HOLD, 20)
motor_.reset_angle(0)
drive_base.use_gyro(True)
drive_base.settings(straight_speed=900)
motor_.run_target(500, -10)
drive_base.straight(55)
drive_base.turn(90)
drive_base.straight(415)
motor_.run_angle(500, -110)
drive_base.straight(-95)
motor_.run_angle(500, 90)
drive_base.turn(-90)
drive_base.straight(114)
drive_base.turn(90)
drive_base.straight(225)
drive_base.straight(-100)
drive_base.turn(-90)
drive_base.straight(70)
drive_base.turn(90)
drive_base.straight(1100)
